1061                 if (result != null) {
1062                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1063                     if (DecodeDecryptedContent.Initialize()) {
1064                         ArrayList arList = new ArrayList();
1065 
1066                         string[] ar = null;
