1073                         string noteId = "";
1074                         string form = "";
1075                         string size = "";
1076                         string url = "";
1077                         string created = "";
1078                         string modified = "";
1079                         List<string> fList = new List<String>();
1080 
1081                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1082                         arList.AddRange(ar);
1083 
1084                         foreach (string str in arList) {
1085                             //YES/NO
1086                             if (str.StartsWith("Error: ")) {
1087                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
1088                                     isError = true;
1089                                 }
1090                             }
1091                             if (str.StartsWith("Message: ")) {
1092                                 message = str.Replace("Message: ", "");
1093                             }
1094                             if (str.StartsWith("Details: ")) {
1095                                 details = str.Replace("Details: ", "");
1096                             }
1097 
1098                             if (str.StartsWith("NoteID: ")) {
1099                                 value = str.Replace("NoteID: ", "");
1100                                 if (!string.IsNullOrEmpty(value)) {
1101                                     noteId = value;
1102                                 }
1103                             }
1104                             if (str.StartsWith("Unid: ")) {
1105                                 value = str.Replace("Unid: ", "");
1106                                 if (!string.IsNullOrEmpty(value)) {
1107                                     unid = value;
1108                                 }
1109                             }
1110                             if (str.StartsWith("Form: ")) {
1111                                 value = str.Replace("Form: ", "");
1112                                 if (!string.IsNullOrEmpty(value)) {
1113                                     form = value;
1114                                 }
1115                             }
1116 
1117                             if (str.StartsWith("Size: ")) {
1118                                 value = str.Replace("Size: ", "");
1119                                 if (!string.IsNullOrEmpty(value)) {
1120                                     size = value;
1121                                 }
1122                             }
1123 
1124                             if (str.StartsWith("URL: ")) {
1125                                 value = str.Replace("URL: ", "");
1126                                 if (!string.IsNullOrEmpty(value)) {
1127                                     url = value;
1128                                 }
1129                             }
1130 
1131                             if (str.StartsWith("Created: ")) {
1132                                 value = str.Replace("Created: ", "");
1133                                 if (!string.IsNullOrEmpty(value)) {
1134                                     created = value;
1135                                 }
1136                             }
1137 
1138                             if (str.StartsWith("Modified: ")) {
1139                                 value = str.Replace("Modified: ", "");
1140                                 if (!string.IsNullOrEmpty(value)) {
1141                                     modified = value;
1142                                 }
1143                             }
1144 
1145                             if (str.StartsWith("FileObject: ")) {
1146                                 value = str.Replace("FileObject: ", "");
1147                                 if (!string.IsNullOrEmpty(value)) {
1148                                     // files = value;
1149                                     fList.Add(value);
1150                                 }
1151                             }
1152                         }
1153 
1154                         if (isError) {
1155                             Connector.ReturnMessages.Add(message);
1156                             Connector.ReturnMessages.Add(details);
1157                             Connector.HasError = true; //throws exception
1158                             return false;
1159                         } else {
