1166                                 docObj.UniversalID = unid;
1167                                 docObj.NoteID = noteId;
1168                                 docObj.Size = size;
1169                                 docObj.Url = url;
1170                                 docObj.Form = form;
1171                                 docObj.DateCreated = created;
1172                                 docObj.DateModified = modified;
1173                                 //create file objects
1174                                 SortedDictionary<string, FileObject> files = new SortedDictionary<string, FileObject>();
1175                                 FileObject fObj = null;
1176                                 foreach (string str in fList) {
1177                                     if (!string.IsNullOrEmpty(str) && !str.Contains("<NO_FILES_ATTACHED>")) {
1178                                         //can be valid file here split on  and count
1179                                         //fObj.Application + "" + fObj.Creator + "" + fObj.DateCreated + "" + fObj.DateModfied + "$" + fObj.FieldName + "" + fObj.FileExtension + "" + fObj.FileName + "$" + fObj.FileSize + "$" + fObj.LinkToFile + "$" + fObj.Other + "$" + fObj.SoftClass);
1180                                         fObj = new FileObject(docObj);
1181                                         if (fObj.Initialize(str)) {
1182                                             files.Add(fObj.Name, fObj);
1183                                         }
1184                                     }
1185                                 }
1186                                 docObj.Files = files;
1187                                 if (dbObj.Documents.ContainsKey(unid)) {
1188                                     dbObj.Documents.Remove(unid);
1189                                 }
1190                                 dbObj.Documents.Add(unid, docObj);
1191                                 Connector.ReturnMessages.Add(message);
1192                                 Connector.ReturnMessages.Add(details);
1193                                 Connector.HasError = false; //SUCCESS
1194                             } else {
