1246     protected internal bool ExecuteDocumentFilesFieldsRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, string fields, DatabaseObject dbObj, DocumentObject docObj) {
1247         HttpWebRequest request = null;
1248 
1249         try {
1250             if (!isInitialized) {
1251                 Connector.ReturnMessages.Add("Unable to execute the DocumentFilesFields Request - Not Initialized/Connected To Server");
1252                 Connector.HasError = true;
1253                 return false;
1254             }
1255 
1256             // ServicePointManager.Expect100Continue = false;
1257 
1258             //create an HTTP request
1259 
1260             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_FieldsFiles");
1261 
1262             request.CookieContainer = this.AuthenticationCookie;
1263             //  request.KeepAlive = True
1264             request.AllowAutoRedirect = true;
1265 
1266             request.Method = WebRequestMethods.Http.Post;   //"POST"
1267             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1268                                                                     // Set the request stream
1269             string result;
1270 
1271             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1272             if (!AddIdentityHeader(ref request)) {
1273                 Connector.ReturnMessages.Add("Unable to Execute DocumentFilesFields Request : Identity Header could not be added!");
1274                 Connector.HasError = true;  //throws error
1275                 return false;
1276             }
1277 
1278             //test with encryption
1279             StringBuilder sb = new StringBuilder();
1280 
1281             sb.AppendLine("UserName: " + Connection.UserName);
1282             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
1283             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
1284             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
1285             sb.AppendLine("UniversalID: " + Unid);
1286             sb.AppendLine("SearchField: " + searchField);
1287             sb.AppendLine("SearchValue: " + searchValue);
1288             sb.AppendLine("Formula: " + formula);
1289             sb.AppendLine("Fields: " + fields);
1290             sb.AppendLine("Connecting via XPagesAPI");
1291 
1292             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1293             string myString = Encoding.UTF8.GetString(b);
1294             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1295             if (EncodedEncryptedContent.Initialize()) {
1296                 Stream requestStream = null;
1297                 try {
1298                     requestStream = request.GetRequestStream();
1299                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
1300                         writer.Write(EncodedEncryptedContent.EncodedContent);
1301                     }
1302                 } catch (Exception e) {
1303                     Connector.ReturnMessages.Add("Unable to execute the DocumentFilesFields Request : " + Common.GetErrorInfo(e));
1304                     Connector.HasError = true;  //throws exception
1305                     return false;
1306                 } finally {
1307                     //if (requestStream != null) {
1308                     //    requestStream.Dispose();
1309                     //}
1310                 }
1311             } else {
1312                 Connector.ReturnMessages.Add("Executing DocumentFilesFields Request - Unable to encode/encrypt the content of the request!");
1313                 Connector.HasError = true; //throws exception
1314                 return false;
1315             }
1316 
1317             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1318 
1319             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
1320                 Stream responseStream = null;
1321                 try {
1322                     responseStream = request.GetResponse().GetResponseStream();
1323                     using (StreamReader reader = new StreamReader(responseStream, true)) {
1324                         result = reader.ReadToEnd();
1325                     }
1326                 } catch (Exception e) {
1327                     Connector.ReturnMessages.Add("Unable to execute the DocumentFilesFields Request : " + Common.GetErrorInfo(e));
1328                     Connector.HasError = true;  //throws exception
1329                     return false;
1330                 } finally {
1331                     //if (responseStream != null) {
1332                     //    responseStream.Dispose();
1333                     //}
1334                 }
1335 
1336                 if (result != null) {
1337                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1338                     if (DecodeDecryptedContent.Initialize()) {
1339                         ArrayList arList = new ArrayList();
1340 
1341                         string[] ar = null;
1342                         bool isError = false;
1343 
1344                         string message = "";
1345                         string details = "";
1346                         string value = "";
1347                         string unid = "";
1348                         string noteId = "";
1349                         string form = "";
1350                         string size = "";
1351                         string url = "";
1352                         string created = "";
1353                         string modified = "";
1354 
1355                         string[] fieldnames = null;
1356                         string[] fieldvalues = null;
1357                         string[] fieldtypes = null;
1358 
1359                         string[] sep = { "||" };
1360 
1361                         List<string> fList = new List<String>();
1362 
1363                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1364                         arList.AddRange(ar);
1365 
1366                         foreach (string str in arList) {
1367                             //YES/NO
1368                             if (str.StartsWith("Error: ")) {
1369                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
1370                                     isError = true;
1371                                 }
1372                             }
1373                             if (str.StartsWith("Message: ")) {
1374                                 message = str.Replace("Message: ", "");
1375                             }
1376                             if (str.StartsWith("Details: ")) {
1377                                 details = str.Replace("Details: ", "");
1378                             }
1379 
1380                             if (str.StartsWith("NoteID: ")) {
1381                                 value = str.Replace("NoteID: ", "");
1382                                 if (!string.IsNullOrEmpty(value)) {
1383                                     noteId = value;
1384                                 }
1385                             }
1386                             if (str.StartsWith("Unid: ")) {
1387                                 value = str.Replace("Unid: ", "");
1388                                 if (!string.IsNullOrEmpty(value)) {
1389                                     unid = value;
1390                                 }
1391                             }
1392                             if (str.StartsWith("Form: ")) {
1393                                 value = str.Replace("Form: ", "");
1394                                 if (!string.IsNullOrEmpty(value)) {
1395                                     form = value;
1396                                 }
1397                             }
1398 
1399                             if (str.StartsWith("Size: ")) {
1400                                 value = str.Replace("Size: ", "");
1401                                 if (!string.IsNullOrEmpty(value)) {
1402                                     size = value;
1403                                 }
1404                             }
1405 
1406                             if (str.StartsWith("URL: ")) {
1407                                 value = str.Replace("URL: ", "");
1408                                 if (!string.IsNullOrEmpty(value)) {
1409                                     url = value;
1410                                 }
1411                             }
1412 
1413                             if (str.StartsWith("Created: ")) {
1414                                 value = str.Replace("Created: ", "");
1415                                 if (!string.IsNullOrEmpty(value)) {
1416                                     created = value;
1417                                 }
1418                             }
1419 
1420                             if (str.StartsWith("Modified: ")) {
1421                                 value = str.Replace("Modified: ", "");
1422                                 if (!string.IsNullOrEmpty(value)) {
1423                                     modified = value;
1424                                 }
1425                             }
1426 
1427                             if (str.StartsWith("FileObject: ")) {
1428                                 value = str.Replace("FileObject: ", "");
1429                                 if (!string.IsNullOrEmpty(value)) {
1430                                     // files = value;
1431                                     fList.Add(value);
1432                                 }
1433                             }
1434 
1435                             if (str.StartsWith("FieldNames: ")) {
1436                                 value = str.Replace("FieldNames: ", "");
1437                                 if (!string.IsNullOrEmpty(value)) {
1438                                     fieldnames = value.Split(sep, StringSplitOptions.None);
1439                                 }
1440                             }
1441 
1442                             if (str.StartsWith("FieldValues: ")) {
1443                                 value = str.Replace("FieldValues: ", "");
1444                                 if (!string.IsNullOrEmpty(value)) {
1445                                     fieldvalues = value.Split(sep, StringSplitOptions.None);
1446                                 }
1447                             }
1448 
1449                             if (str.StartsWith("FieldTypes: ")) {
1450                                 value = str.Replace("FieldTypes: ", "");
1451                                 if (!string.IsNullOrEmpty(value)) {
1452                                     fieldtypes = value.Split(sep, StringSplitOptions.None);
1453                                 }
1454                             }
1455 
1456                           
1457                         }
1458 
1459                         if (isError) {
1460                             Connector.ReturnMessages.Add(message);
1461                             Connector.ReturnMessages.Add(details);
1462                             Connector.HasError = true; //throws exception
1463                             return false;
1464                         } else {
1465                             // add doc to list
1466                             if (dbObj.Documents == null) {
1467                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1468                             }
1469                             if (!string.IsNullOrEmpty(unid)) {
1470                                 //docObj = new DocumentObject(dbObj, unid);
1471                                 docObj.UniversalID = unid;
1472                                 docObj.NoteID = noteId;
1473                                 docObj.Size = size;
1474                                 docObj.Url = url;
1475                                 docObj.Form = form;
1476                                 docObj.DateCreated = created;
1477                                 docObj.DateModified = modified;
1478                                 //create file objects
1479                                 SortedDictionary<string, FileObject> files = new SortedDictionary<string, FileObject>();
1480                                 FileObject fObj = null;
1481                                 foreach (string str in fList) {
1482                                     if (!string.IsNullOrEmpty(str) && !str.Contains("<NO_FILES_ATTACHED>")) {
1483                                         //can be valid file here split on  and count
1484                                         //fObj.Application + "" + fObj.Creator + "" + fObj.DateCreated + "" + fObj.DateModfied + "$" + fObj.FieldName + "" + fObj.FileExtension + "" + fObj.FileName + "$" + fObj.FileSize + "$" + fObj.LinkToFile + "$" + fObj.Other + "$" + fObj.SoftClass);
1485                                         fObj = new FileObject(docObj);
1486                                         if (fObj.Initialize(str)) {
1487                                             files.Add(fObj.Name, fObj);
1488                                         }
1489                                     }
1490                                 }
1491                                 docObj.Files = files;
1492 
1493                                 // create fields
1494                                 FieldObject fieldObj = null;
1495                                 if (docObj.Fields == null) {
1496                                     docObj.Fields = new SortedDictionary<string, FieldObject>();
1497                                 }
1498                                 for (int i = 0; i < fieldnames.Length; i++) {
1499                                     fieldObj = new FieldObject(fieldnames[i]);
1500                                     if (fieldvalues.Length - 1 >= i) {
1501                                         fieldObj.Value = fieldvalues[i];
1502                                     }
1503                                     if (fieldtypes.Length - 1 >= i) {
1504                                         fieldObj.Type = fieldtypes[i];
1505                                     }
1506 
1507                                     if (docObj.Fields.ContainsKey(fieldObj.Name)) {
1508                                         docObj.Fields.Remove(fieldObj.Name);
1509                                     }
1510                                     docObj.Fields.Add(fieldObj.Name, fieldObj);
1511                                 }
1512 
1513                                 if (dbObj.Documents.ContainsKey(unid)) {
1514                                     dbObj.Documents.Remove(unid);
1515                                 }
1516                                 dbObj.Documents.Add(unid, docObj);
1517 
1518                                 Connector.ReturnMessages.Add(message);
1519                                 Connector.ReturnMessages.Add(details);
1520                                 Connector.HasError = false; //SUCCESS
1521                             } else {
1522                                 // unid not found??
1523                                 Connector.ReturnMessages.Add(message);
1524                                 Connector.ReturnMessages.Add(details);
1525                                 Connector.ReturnMessages.Add("Universal ID not found!");
1526                                 Connector.HasError = true; // throws exception
1527                             }
1528                         }
1529                     } else {
1530                         Connector.ReturnMessages.Add("Executing DocumentFilesFields Request - Unable to decode/decrypt the content of the response!");
1531                         Connector.HasError = true;  //throws exception
1532                         return false;
1533                     }
1534                 } else {
1535                     //no response from server
1536                     Connector.ReturnMessages.Add("Executing DocumentFilesFields Request -  Unable to get response from XPages!");
1537                     Connector.HasError = true;  //throws exception
1538                     return false;
1539                 }
1540                 return true;
1541             } else {
1542                 Connector.ReturnMessages.Add("Executing DocumentFilesFields Request -  Unable to get the Document - Authentication Issue");
1543                 Connector.HasError = true;  //throws exception
1544                 return false;
1545             }
1546         } catch (Exception ex) {
1547             if (ex.GetType() == typeof(WebException)) {
1548                 WebException webex;
1549                 webex = (WebException)ex;
1550                 if (webex.Response != null) {
1551                     Stream stream = webex.Response.GetResponseStream();
1552                     using (StreamReader reader = new StreamReader(stream)) {
1553                         string errorResp = reader.ReadToEnd();
1554                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1555                         Connector.ReturnMessages.Add("Unable to Execute the DocumentFilesFields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1556                         Connector.HasError = true;  //throws exception
1557                         return false;
1558                     }
1559                 } else {
1560                     Connector.ReturnMessages.Add("Unable to Execute the DocumentFilesFields Request : " + Common.GetErrorInfo(ex));
1561                     Connector.HasError = true;  //throws exception
1562                     return false;
1563                 }
1564             } else {
1565                 Connector.ReturnMessages.Add("Unable to Execute the DocumentFilesFields Request : " + Common.GetErrorInfo(ex));
1566                 Connector.HasError = true;  //throws exception
1567                 return false;
1568             }
1569         }
1570     }
