1828                     } else {
1829                         Connector.ReturnMessages.Add("Executing All Documents Request - Unable to decode/decrypt the content of the response!");
1830                         Connector.HasError = true;  //throws exception
1831                         return false;
1832                     }
1833                 } else {
1834                     //no response from server
1835                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get response from XPages!");
1836                     Connector.HasError = true;  //throws exception
1837                     return false;
1838                 }
1839                 return true;
1840             } else {
1841                 Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Document - Authentication Issue");
1842                 Connector.HasError = true;  //throws exception
1843                 return false;
1844             }
1845         } catch (Exception ex) {
1846             if (ex.GetType() == typeof(WebException)) {
1847                 WebException webex;
1848                 webex = (WebException)ex;
1849                 if (webex.Response != null) {
1850                     Stream stream = webex.Response.GetResponseStream();
1851                     using (StreamReader reader = new StreamReader(stream)) {
1852                         string errorResp = reader.ReadToEnd();
1853                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1854                         Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1855                         Connector.HasError = true;  //throws exception
1856                         return false;
1857                     }
1858                 } else {
1859                     Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1860                     Connector.HasError = true;  //throws exception
1861                     return false;
1862                 }
1863             } else {
1864                 Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1865                 Connector.HasError = true;  //throws exception
1866                 return false;
1867             }
1868         }
1869     }
