1871     protected internal bool ExecuteAllDocumentsFilesRequest(string WebServiceURL, string searchField, string searchValue, string formula, string unids, DatabaseObject dbObj) {
1872         HttpWebRequest request = null;
1873 
1874         try {
1875             if (!isInitialized) {
1876                 Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request - Not Initialized/Connected To Server");
1877                 Connector.HasError = true;
1878                 return false;
1879             }
1880 
1881             //    ServicePointManager.Expect100Continue = false;
1882 
1883             //create an HTTP request
1884 
1885             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFiles");
1886             //request.Timeout = 300000; // set timeout to 5minutes
1887             request.Timeout = 1800000; //half an hour!
1888             request.CookieContainer = this.AuthenticationCookie;
1889             //  request.KeepAlive = True
1890             request.AllowAutoRedirect = true;
1891 
1892             request.Method = WebRequestMethods.Http.Post;   //"POST"
1893             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1894                                                                     // Set the request stream
1895             string result;
1896 
1897             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1898             if (!AddIdentityHeader(ref request)) {
1899                 Connector.ReturnMessages.Add("Unable to Execute AllDocumentsFiles Request : Identity Header could not be added!");
1900                 Connector.HasError = true;  //throws error
1901                 return false;
1902             }
1903 
1904             //test with encryption
1905             StringBuilder sb = new StringBuilder();
1906 
1907             sb.AppendLine("UserName: " + Connection.UserName);
1908             sb.AppendLine("ServerName: " + dbObj.ServerName);
1909             sb.AppendLine("FilePath: " + dbObj.FilePath);
1910             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
1911             sb.AppendLine("SearchField: " + searchField);
1912             sb.AppendLine("SearchValue: " + searchValue);
1913             sb.AppendLine("Formula: " + formula);
1914             sb.AppendLine("Unids: " + unids);
1915             sb.AppendLine("Connecting via XPagesAPI");
1916 
1917             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1918             string myString = Encoding.UTF8.GetString(b);
1919             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1920             if (EncodedEncryptedContent.Initialize()) {
1921                 Stream requestStream = null;
1922                 try {
1923                     requestStream = request.GetRequestStream();
1924                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
1925                         writer.Write(EncodedEncryptedContent.EncodedContent);
1926                     }
1927                 } catch (Exception e) {
1928                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1929                     Connector.HasError = true;  //throws exception
1930                     return false;
1931                 } finally {
1932                     //if (requestStream != null) {
1933                     //    requestStream.Dispose();
1934                     //}
1935                 }
1936             } else {
1937                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to encode/encrypt the content of the request!");
1938                 Connector.HasError = true; //throws exception
1939                 return false;
1940             }
1941 
1942             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1943 
1944             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
1945                 Stream responseStream = null;
1946                 try {
1947                     responseStream = request.GetResponse().GetResponseStream();
1948                     using (StreamReader reader = new StreamReader(responseStream, true)) {
1949                         result = reader.ReadToEnd();
1950                     }
1951                 } catch (Exception e) {
1952                     Connector.ReturnMessages.Add("Unable to execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(e));
1953                     Connector.HasError = true;  //throws exception
1954                     return false;
1955                 } finally {
1956                     //if (responseStream != null) {
1957                     //    responseStream.Dispose();
1958                     //}
1959                 }
1960 
1961                 if (result != null) {
1962                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1963                     if (DecodeDecryptedContent.Initialize()) {
1964                         ArrayList arList = new ArrayList();
1965 
1966                         string[] ar = null;
1967                         bool isError = false;
1968 
1969                         string message = "";
1970                         string details = "";
1971                         string value = "";
1972                         string[] sep = { "||" };
1973                         string[] NoteIds = { };
1974                         string[] Unids = { };
1975                         string[] Forms = { };
1976                         string[] Sizes = { };
1977                         string[] URLs = { };
1978                         string[] Created = { };
1979                         string[] Modified = { };
1980                         string[] Files = { };
1981 
1982                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1983                         arList.AddRange(ar);
1984                         foreach (string str in arList) {
1985                             //YES/NO
1986                             if (str.StartsWith("Error: ")) {
1987                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
1988                                     isError = true;
1989                                 }
1990                             }
1991                             if (str.StartsWith("Message: ")) {
1992                                 message = str.Replace("Message: ", "");
1993                             }
1994                             if (str.StartsWith("Details: ")) {
1995                                 details = str.Replace("Details: ", "");
1996                             }
1997 
1998                             if (str.StartsWith("Unids: ")) {
1999                                 value = str.Replace("Unids: ", "");
2000                                 if (!string.IsNullOrEmpty(value)) {
2001                                     Unids = value.Split(sep, StringSplitOptions.None);
2002                                 }
2003                             }
2004 
2005                             if (str.StartsWith("NoteIDs: ")) {
2006                                 value = str.Replace("NoteIDs: ", "");
2007                                 if (!string.IsNullOrEmpty(value)) {
2008                                     NoteIds = value.Split(sep, StringSplitOptions.None);
2009                                 }
2010                             }
2011 
2012                             if (str.StartsWith("Forms: ")) {
2013                                 value = str.Replace("Forms: ", "");
2014                                 if (!string.IsNullOrEmpty(value)) {
2015                                     Forms = value.Split(sep, StringSplitOptions.None);
2016                                 }
2017                             }
2018 
2019                             if (str.StartsWith("Sizes: ")) {
2020                                 value = str.Replace("Sizes: ", "");
2021                                 if (!string.IsNullOrEmpty(value)) {
2022                                     Sizes = value.Split(sep, StringSplitOptions.None);
2023                                 }
2024                             }
2025 
2026                             if (str.StartsWith("URLs: ")) {
2027                                 value = str.Replace("URLs: ", "");
2028                                 if (!string.IsNullOrEmpty(value)) {
2029                                     URLs = value.Split(sep, StringSplitOptions.None);
2030                                 }
2031                             }
2032 
2033                             if (str.StartsWith("Created: ")) {
2034                                 value = str.Replace("Created: ", "");
2035                                 if (!string.IsNullOrEmpty(value)) {
2036                                     Created = value.Split(sep, StringSplitOptions.None);
2037                                 }
2038                             }
2039 
2040                             if (str.StartsWith("Modified: ")) {
2041                                 value = str.Replace("Modified: ", "");
2042                                 if (!string.IsNullOrEmpty(value)) {
2043                                     Modified = value.Split(sep, StringSplitOptions.None);
2044                                 }
2045                             }
2046 
2047                             if (str.StartsWith("FileObjects: ")) {
2048                                 value = str.Replace("FileObjects: ", "");
2049                                 if (!string.IsNullOrEmpty(value)) {
2050                                     Files = value.Split(sep, StringSplitOptions.None);
2051                                 }
2052                             }
2053                         }
2054 
2055                         //loop through unids, notesids, forms, sizes, urls and create document objects
2056                         // check if all have the same number of items!
2057                         int count = 0;
2058                         if (Unids != null && Unids.Length > 0) {
2059                             count = Unids.Length;
2060                             if (NoteIds != null && NoteIds.Length > 0) {
2061                                 if (count != NoteIds.Length) {
2062                                     Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Documents - Unids <> NoteIds");
2063                                     Connector.HasError = true;  //throws exception
2064                                     return false;
2065                                 } else {
2066                                     // form can be empty!!
2067                                     //if (Forms != null && Forms.Length > 0) {
2068                                     //    if (count != Forms.Length) {
2069                                     //        Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Forms");
2070                                     //        Connector.hasError = true;  //throws exception
2071                                     //        return false;
2072                                     //    } else {
2073                                     if (Sizes != null && Sizes.Length > 0) {
2074                                         if (count != Sizes.Length) {
2075                                             Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Documents - Unids <> Sizes");
2076                                             Connector.HasError = true;  //throws exception
2077                                             return false;
2078                                         } else {
2079                                             if (URLs != null && URLs.Length > 0) {
2080                                                 if (count != URLs.Length) {
2081                                                     Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Documents - Unids <> URLs");
2082                                                     Connector.HasError = true;  //throws exception
2083                                                     return false;
2084                                                 }
2085                                             }
2086                                         }
2087                                     }
2088                                     //   }
2089                                     // }
2090                                 }
2091                             }
2092                             // loop through and create doc obj
2093                             DocumentObject docObj = null;
2094                             if (dbObj.Documents == null) {
2095                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
2096                             }
2097                             for (int i = 0; i < Unids.Length; i++) {
2098                                 docObj = new DocumentObject(dbObj, Unids[i]);
2099                                 if (NoteIds.Length - 1 >= i) {
2100                                     docObj.NoteID = NoteIds[i];
2101                                 }
2102                                 if (Forms.Length - 1 >= i) {
2103                                     docObj.Form = Forms[i];
2104                                 }
2105                                 if (Sizes.Length - 1 >= i) {
2106                                     docObj.Size = Sizes[i];
2107                                 }
2108                                 if (URLs.Length - 1 >= i) {
2109                                     docObj.Url = URLs[i];
2110                                 }
2111                                 if (Created.Length - 1 >= i) {
2112                                     docObj.DateCreated = Created[i];
2113                                 }
2114                                 if (Modified.Length - 1 >= i) {
2115                                     docObj.DateModified = Modified[i];
2116                                 }
2117                                 if (Files.Length - 1 >= i) {
2118                                     //docObj.DateModified = Files[i];
2119                                     //get all files into fileobjects here - split on 'FileObject: '
2120                                     docObj.Files = new SortedDictionary<string, FileObject>();
2121                                     FileObject fObj = null;
2122                                     string[] arFiles = Files[i].Split(new[] { "FileObject: " }, StringSplitOptions.RemoveEmptyEntries);
2123 
2124                                     foreach (string str in arFiles) {
2125                                         if (!string.IsNullOrEmpty(str) && !str.Contains("<NO_FILES_ATTACHED>")) {
2126                                             //can be valid file here split on  and count
2127                                             //fObj.Application + "" + fObj.Creator + "" + fObj.DateCreated + "" + fObj.DateModfied + "$" + fObj.FieldName + "" + fObj.FileExtension + "" + fObj.FileName + "" + fObj.FileSize + "" + fObj.LinkToFile + "" + fObj.Other + "" + fObj.SoftClass);
2128                                             fObj = new FileObject(docObj);
2129                                             if (fObj.Initialize(str)) {
2130                                                 docObj.Files.Add(fObj.Name, fObj);
2131                                             }
2132                                         }
2133                                     }
2134                                 }
2135                                 if (dbObj.Documents.ContainsKey(Unids[i])) {
2136                                     dbObj.Documents.Remove(Unids[i]);
2137                                 }
2138                                 docObj.IsInitialized = true;
2139                                 dbObj.Documents.Add(Unids[i], docObj);
2140                             }
2141                         }
2142 
2143                         if (isError) {
2144                             Connector.ReturnMessages.Add(message);
2145                             Connector.ReturnMessages.Add(details);
2146                             Connector.HasError = true; //throws exception
2147                             return false;
2148                         } else {
2149                             Connector.ReturnMessages.Add(message);
2150                             Connector.ReturnMessages.Add(details);
2151                             Connector.HasError = false; //SUCCESS
2152                         }
2153                     } else {
2154                         Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to decode/decrypt the content of the response!");
2155                         Connector.HasError = true;  //throws exception
2156                         return false;
2157                     }
2158                 } else {
2159                     //no response from server
2160                     Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get response from XPages!");
2161                     Connector.HasError = true;  //throws exception
2162                     return false;
2163                 }
2164                 return true;
2165             } else {
2166                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Document - Authentication Issue");
2167                 Connector.HasError = true;  //throws exception
2168                 return false;
2169             }
2170         } catch (Exception ex) {
2171             if (ex.GetType() == typeof(WebException)) {
2172                 WebException webex;
2173                 webex = (WebException)ex;
2174                 if (webex.Response != null) {
2175                     Stream stream = webex.Response.GetResponseStream();
2176                     using (StreamReader reader = new StreamReader(stream)) {
2177                         string errorResp = reader.ReadToEnd();
2178                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2179                         Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2180                         Connector.HasError = true;  //throws exception
2181                         return false;
2182                     }
2183                 } else {
2184                     Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2185                     Connector.HasError = true;  //throws exception
2186                     return false;
2187                 }
2188             } else {
2189                 Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
2190                 Connector.HasError = true;  //throws exception
2191                 return false;
2192             }
2193         }
2194     }
