2399                     return false;
2400                 }
2401             } else {
2402                 Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2403                 Connector.HasError = true;  //throws exception
2404                 return false;
2405             }
2406         }
2407     }
