2409     protected internal bool ExecuteAllFilesRequest(string WebServiceURL, DatabaseObject dbObj) {
2410         HttpWebRequest request = null;
2411 
2412         try {
2413             if (!isInitialized) {
2414                 Connector.ReturnMessages.Add("Unable to execute the All Files Request - Not Initialized/Connected To Server");
2415                 Connector.HasError = true;
2416                 return false;
2417             }
2418 
2419             //  ServicePointManager.Expect100Continue = false;
2420 
2421             //create an HTTP request
2422 
2423             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFiles");
2424             //request.Timeout = 300000; // set timeout to 5minutes
2425             request.Timeout = 1800000; //half an hour!
2426             request.CookieContainer = this.AuthenticationCookie;
2427             //  request.KeepAlive = True
2428             request.AllowAutoRedirect = true;
2429 
2430             request.Method = WebRequestMethods.Http.Post;   //"POST"
2431             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2432                                                                     // Set the request stream
2433             string result;
2434 
2435             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2436             if (!AddIdentityHeader(ref request)) {
2437                 Connector.ReturnMessages.Add("Unable to Execute All Files Request : Identity Header could not be added!");
2438                 Connector.HasError = true;  //throws error
2439                 return false;
2440             }
2441 
2442             //test with encryption
2443             StringBuilder sb = new StringBuilder();
2444 
2445             sb.AppendLine("UserName: " + Connection.UserName);
2446             sb.AppendLine("ServerName: " + dbObj.ServerName);
2447             sb.AppendLine("FilePath: " + dbObj.FilePath);
2448             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
2449             //sb.AppendLine("SearchField: " + searchField);
2450             sb.AppendLine("SearchField: ");
2451             //sb.AppendLine("SearchValue: " + searchValue);
2452             sb.AppendLine("SearchValue: ");
2453             //sb.AppendLine("Formula: " + formula);
2454             sb.AppendLine("Formula: ");
2455             string listUnids = String.Join(";", dbObj.Documents.Keys.ToArray());
2456 
2457             sb.AppendLine("Unids: " + listUnids);
2458             sb.AppendLine("Connecting via XPagesAPI");
2459 
2460             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2461             string myString = Encoding.UTF8.GetString(b);
2462             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2463             if (EncodedEncryptedContent.Initialize()) {
2464                 Stream requestStream = null;
2465                 try {
2466                     requestStream = request.GetRequestStream();
2467                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
2468                         writer.Write(EncodedEncryptedContent.EncodedContent);
2469                     }
2470                 } catch (Exception e) {
2471                     Connector.ReturnMessages.Add("Unable to execute the All Files Request : " + Common.GetErrorInfo(e));
2472                     Connector.HasError = true;  //throws exception
2473                     return false;
2474                 } finally {
2475                     //if (requestStream != null) {
2476                     //    requestStream.Dispose();
2477                     //}
2478                 }
2479             } else {
2480                 Connector.ReturnMessages.Add("Executing All Files Request - Unable to encode/encrypt the content of the request!");
2481                 Connector.HasError = true; //throws exception
2482                 return false;
2483             }
2484 
2485             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2486 
2487             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
2488                 Stream responseStream = null;
2489                 try {
2490                     responseStream = request.GetResponse().GetResponseStream();
2491                     using (StreamReader reader = new StreamReader(responseStream, true)) {
2492                         result = reader.ReadToEnd();
2493                     }
2494                 } catch (Exception e) {
2495                     Connector.ReturnMessages.Add("Unable to execute the All Files Request : " + Common.GetErrorInfo(e));
2496                     Connector.HasError = true;  //throws exception
2497                     return false;
2498                 } finally {
2499                     //if (responseStream != null) {
2500                     //    responseStream.Dispose();
2501                     //}
2502                 }
2503 
2504                 if (result != null) {
2505                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2506                     if (DecodeDecryptedContent.Initialize()) {
2507                         ArrayList arList = new ArrayList();
2508 
2509                         string[] ar = null;
2510                         bool isError = false;
2511 
2512                         string message = "";
2513                         string details = "";
2514                         string value = "";
2515                         string[] sep = { "||" };
2516                         string[] NoteIds = { };
2517                         string[] Unids = { };
2518                         string[] Forms = { };
2519                         string[] Sizes = { };
2520                         string[] URLs = { };
2521                         string[] Created = { };
2522                         string[] Modified = { };
2523                         string[] Files = { };
2524 
2525                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2526                         arList.AddRange(ar);
2527                         foreach (string str in arList) {
2528                             //YES/NO
2529                             if (str.StartsWith("Error: ")) {
2530                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
2531                                     isError = true;
2532                                 }
2533                             }
2534                             if (str.StartsWith("Message: ")) {
2535                                 message = str.Replace("Message: ", "");
2536                             }
2537                             if (str.StartsWith("Details: ")) {
2538                                 details = str.Replace("Details: ", "");
2539                             }
2540 
2541                             if (str.StartsWith("Unids: ")) {
2542                                 value = str.Replace("Unids: ", "");
2543                                 if (!string.IsNullOrEmpty(value)) {
2544                                     Unids = value.Split(sep, StringSplitOptions.None);
2545                                 }
2546                             }
2547 
2548                             if (str.StartsWith("NoteIDs: ")) {
2549                                 value = str.Replace("NoteIDs: ", "");
2550                                 if (!string.IsNullOrEmpty(value)) {
2551                                     NoteIds = value.Split(sep, StringSplitOptions.None);
2552                                 }
2553                             }
2554 
2555                             if (str.StartsWith("Forms: ")) {
2556                                 value = str.Replace("Forms: ", "");
2557                                 if (!string.IsNullOrEmpty(value)) {
2558                                     Forms = value.Split(sep, StringSplitOptions.None);
2559                                 }
2560                             }
2561 
2562                             if (str.StartsWith("Sizes: ")) {
2563                                 value = str.Replace("Sizes: ", "");
2564                                 if (!string.IsNullOrEmpty(value)) {
2565                                     Sizes = value.Split(sep, StringSplitOptions.None);
2566                                 }
2567                             }
2568 
2569                             if (str.StartsWith("URLs: ")) {
2570                                 value = str.Replace("URLs: ", "");
2571                                 if (!string.IsNullOrEmpty(value)) {
2572                                     URLs = value.Split(sep, StringSplitOptions.None);
2573                                 }
2574                             }
2575 
2576                             if (str.StartsWith("Created: ")) {
2577                                 value = str.Replace("Created: ", "");
2578                                 if (!string.IsNullOrEmpty(value)) {
2579                                     Created = value.Split(sep, StringSplitOptions.None);
2580                                 }
2581                             }
2582 
2583                             if (str.StartsWith("Modified: ")) {
2584                                 value = str.Replace("Modified: ", "");
2585                                 if (!string.IsNullOrEmpty(value)) {
2586                                     Modified = value.Split(sep, StringSplitOptions.None);
2587                                 }
2588                             }
2589 
2590                             if (str.StartsWith("FileObjects: ")) {
2591                                 value = str.Replace("FileObjects: ", "");
2592                                 if (!string.IsNullOrEmpty(value)) {
2593                                     Files = value.Split(sep, StringSplitOptions.None);
2594                                 }
2595                             }
2596                         }
2597 
2598                         //loop through unids, notesids, forms, sizes, urls and create document objects
2599                         // check if all have the same number of items!
2600                         int count = 0;
2601                         if (Unids != null && Unids.Length > 0) {
2602                             count = Unids.Length;
2603                             if (NoteIds != null && NoteIds.Length > 0) {
2604                                 if (count != NoteIds.Length) {
2605                                     Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get the Documents - Unids <> NoteIds");
2606                                     Connector.HasError = true;  //throws exception
2607                                     return false;
2608                                 } else {
2609                                     // form can be empty!!
2610                                     //if (Forms != null && Forms.Length > 0) {
2611                                     //    if (count != Forms.Length) {
2612                                     //        Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Forms");
2613                                     //        Connector.hasError = true;  //throws exception
2614                                     //        return false;
2615                                     //    } else {
2616                                     if (Sizes != null && Sizes.Length > 0) {
2617                                         if (count != Sizes.Length) {
2618                                             Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get the Documents - Unids <> Sizes");
2619                                             Connector.HasError = true;  //throws exception
2620                                             return false;
2621                                         } else {
2622                                             if (URLs != null && URLs.Length > 0) {
2623                                                 if (count != URLs.Length) {
2624                                                     Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get the Documents - Unids <> URLs");
2625                                                     Connector.HasError = true;  //throws exception
2626                                                     return false;
2627                                                 }
2628                                             }
2629                                         }
2630                                     }
2631                                     //   }
2632                                     // }
2633                                 }
2634                             }
2635                             // loop through and create doc obj
2636                             DocumentObject docObj = null;
2637                             if (dbObj.Documents == null) {
2638                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
2639                             }
2640                             for (int i = 0; i < Unids.Length; i++) {
2641                                 docObj = new DocumentObject(dbObj, Unids[i]);
2642                                 if (NoteIds.Length - 1 >= i) {
2643                                     docObj.NoteID = NoteIds[i];
2644                                 }
2645                                 if (Forms.Length - 1 >= i) {
2646                                     docObj.Form = Forms[i];
2647                                 }
2648                                 if (Sizes.Length - 1 >= i) {
2649                                     docObj.Size = Sizes[i];
2650                                 }
2651                                 if (URLs.Length - 1 >= i) {
2652                                     docObj.Url = URLs[i];
2653                                 }
2654                                 if (Created.Length - 1 >= i) {
2655                                     docObj.DateCreated = Created[i];
2656                                 }
2657                                 if (Modified.Length - 1 >= i) {
2658                                     docObj.DateModified = Modified[i];
2659                                 }
2660                                 if (Files.Length - 1 >= i) {
2661                                     //docObj.DateModified = Files[i];
2662                                     //get all files into fileobjects here - split on 'FileObject: '
2663                                     docObj.Files = new SortedDictionary<string, FileObject>();
2664                                     FileObject fObj = null;
2665                                     string[] arFiles = Files[i].Split(new[] { "FileObject: " }, StringSplitOptions.RemoveEmptyEntries);
2666 
2667                                     foreach (string str in arFiles) {
2668                                         if (!string.IsNullOrEmpty(str) && !str.Contains("<NO_FILES_ATTACHED>")) {
2669                                             //can be valid file here split on  and count
2670                                             //fObj.Application + "" + fObj.Creator + "" + fObj.DateCreated + "" + fObj.DateModfied + "$" + fObj.FieldName + "" + fObj.FileExtension + "" + fObj.FileName + "" + fObj.FileSize + "" + fObj.LinkToFile + "" + fObj.Other + "" + fObj.SoftClass);
2671                                             fObj = new FileObject(docObj);
2672                                             if (fObj.Initialize(str)) {
2673                                                 docObj.Files.Add(fObj.Name, fObj);
2674                                             }
2675                                         }
2676                                     }
2677                                 }
2678                                 if (dbObj.Documents.ContainsKey(Unids[i])) {
2679                                     dbObj.Documents.Remove(Unids[i]);
2680                                 }
2681                                 docObj.IsInitialized = true;
2682                                 dbObj.Documents.Add(Unids[i], docObj);
2683                             }
2684                         }
2685 
2686                         if (isError) {
2687                             Connector.ReturnMessages.Add(message);
2688                             Connector.ReturnMessages.Add(details);
2689                             Connector.HasError = true; //throws exception
2690                             return false;
2691                         } else {
2692                             Connector.ReturnMessages.Add(message);
2693                             Connector.ReturnMessages.Add(details);
2694                             Connector.HasError = false; //SUCCESS
2695                         }
2696                     } else {
2697                         Connector.ReturnMessages.Add("Executing All Files Request - Unable to decode/decrypt the content of the response!");
2698                         Connector.HasError = true;  //throws exception
2699                         return false;
2700                     }
2701                 } else {
2702                     //no response from server
2703                     Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get response from XPages!");
2704                     Connector.HasError = true;  //throws exception
2705                     return false;
2706                 }
2707                 return true;
2708             } else {
2709                 Connector.ReturnMessages.Add("Executing All Files Request -  Unable to get the Document - Authentication Issue");
2710                 Connector.HasError = true;  //throws exception
2711                 return false;
2712             }
2713         } catch (Exception ex) {
2714             if (ex.GetType() == typeof(WebException)) {
2715                 WebException webex;
2716                 webex = (WebException)ex;
2717                 if (webex.Response != null) {
2718                     Stream stream = webex.Response.GetResponseStream();
2719                     using (StreamReader reader = new StreamReader(stream)) {
2720                         string errorResp = reader.ReadToEnd();
2721                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2722                         Connector.ReturnMessages.Add("Unable to Execute the All Files Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2723                         Connector.HasError = true;  //throws exception
2724                         return false;
2725                     }
2726                 } else {
2727                     Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2728                     Connector.HasError = true;  //throws exception
2729                     return false;
2730                 }
2731             } else {
2732                 Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2733                 Connector.HasError = true;  //throws exception
2734                 return false;
2735             }
2736         }
2737     }
