3495     protected internal bool ExecuteGetFileRequest(string FileURL, string LocalFilePath) {
3496         HttpWebRequest request = null;
3497         try {
3498             if (!isInitialized) {
3499                 Connector.ReturnMessages.Add("Unable to execute the Get File Request - Not Initialized/Connected To Server");
3500                 Connector.HasError = true;
3501                 return false;
3502             }
3503 
3504             request = request = (HttpWebRequest)WebRequest.Create(FileURL);
3505 
3506             request.CookieContainer = AuthenticationCookie;
3507             request.AllowAutoRedirect = true;
3508             request.Method = WebRequestMethods.Http.Get;
3509             const int BUFFER_SIZE = 16 * 1024;
3510             byte[] buffer = new byte[BUFFER_SIZE - 1];
3511             int bytesRead;
3512 
3513             using (var outputFileStream = File.Create(LocalFilePath, BUFFER_SIZE)) {
3514                 using (var response2 = request.GetResponse()) {
3515                     using (var responseStream = response2.GetResponseStream()) {
3516                         while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0) {
3517                             outputFileStream.Write(buffer, 0, bytesRead);
3518                         }
3519                     }
3520                 }
3521             }
3522             return true;
3523         } catch (Exception ex) {
3524             if (ex.GetType() == typeof(WebException)) {
3525                 WebException webex;
3526                 webex = (WebException)ex;
3527                 if (webex.Response != null) {
3528                     Stream stream = webex.Response.GetResponseStream();
3529                     using (StreamReader reader = new StreamReader(stream)) {
3530                         string errorResp = reader.ReadToEnd();
3531                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
3532                         Connector.ReturnMessages.Add("Unable to Execute the Get File Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
3533                         Connector.HasError = true;  //throws exception
3534                         return false;
3535                     }
3536                 } else {
3537                     Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3538                     Connector.HasError = true;  //throws exception
3539                     return false;
3540                 }
3541             } else {
3542                 Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
3543                 Connector.HasError = true;  //throws exception
3544                 return false;
3545             }
3546         }
3547     }
3548 
3549     #endregion Protected Internal Methods
3550 }
