9 internal class Requestor {
10 
11     #region Variables
12 
13     private Connector Connection;
14     protected internal bool isInitialized = false;
15     private CookieContainer AuthenticationCookie;
16 
17     #endregion Variables
18 
19     #region Constructor
20 
21     public Requestor(Connector Connector) {
22         Connection = Connector;
23     }
24 
25     #endregion Constructor
