722     protected internal bool ExecuteDocumentRequest(string WebServiceURL, string Unid, string searchField, string searchValue, string formula, DatabaseObject dbObj, DocumentObject docObj) {
723         HttpWebRequest request = null;
724 
725         try {
726             if (!isInitialized) {
727                 Connector.ReturnMessages.Add("Unable to execute the Document Request - Not Initialized/Connected To Server");
728                 Connector.HasError = true;
729                 return false;
730             }
731 
732             // ServicePointManager.Expect100Continue = false;
733 
734             //create an HTTP request
735 
736             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Document");
737 
738             request.CookieContainer = this.AuthenticationCookie;
739             //  request.KeepAlive = True
740             request.AllowAutoRedirect = true;
741 
742             request.Method = WebRequestMethods.Http.Post;   //"POST"
743             request.ContentType = "application/jpi; charset=utf-8"; //application/json
744                                                                     // Set the request stream
745             string result;
746 
747             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
748             if (!AddIdentityHeader(ref request)) {
749                 Connector.ReturnMessages.Add("Unable to Execute Document Request : Identity Header could not be added!");
750                 Connector.HasError = true;  //throws error
751                 return false;
752             }
753 
754             //test with encryption
755             StringBuilder sb = new StringBuilder();
756 
757             sb.AppendLine("UserName: " + Connection.UserName);
758             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
759             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
760             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
761             sb.AppendLine("UniversalID: " + Unid);
762             sb.AppendLine("SearchField: " + searchField);
763             sb.AppendLine("SearchValue: " + searchValue);
764             sb.AppendLine("Formula: " + formula);
765             sb.AppendLine("Connecting via XPagesAPI");
766 
767             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
768             string myString = Encoding.UTF8.GetString(b);
769             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
770             if (EncodedEncryptedContent.Initialize()) {
771                 Stream requestStream = null;
772                 try {
773                     requestStream = request.GetRequestStream();
774                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
775                         writer.Write(EncodedEncryptedContent.EncodedContent);
776                     }
777                 } catch (Exception e) {
778                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
779                     Connector.HasError = true;  //throws exception
780                     return false;
781                 } finally {
782                     //if (requestStream != null) {
783                     //    requestStream.Dispose();
784                     //}
785                 }
786             } else {
787                 Connector.ReturnMessages.Add("Executing Document Request - Unable to encode/encrypt the content of the request!");
788                 Connector.HasError = true; //throws exception
789                 return false;
790             }
791 
792             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
793 
794             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
795                 Stream responseStream = null;
796                 try {
797                     responseStream = request.GetResponse().GetResponseStream();
798                     using (StreamReader reader = new StreamReader(responseStream, true)) {
799                         result = reader.ReadToEnd();
800                     }
801                 } catch (Exception e) {
802                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
803                     Connector.HasError = true;  //throws exception
804                     return false;
805                 } finally {
806                     //if (responseStream != null) {
807                     //    responseStream.Dispose();
808                     //}
809                 }
810 
811                 if (result != null) {
812                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
813                     if (DecodeDecryptedContent.Initialize()) {
814                         ArrayList arList = new ArrayList();
815 
816                         string[] ar = null;
817                         bool isError = false;
818 
819                         string message = "";
820                         string details = "";
821                         string value = "";
822                         string unid = "";
823                         string noteId = "";
824                         string form = "";
825                         string size = "";
826                         string url = "";
827                         string created = "";
828                         string modified = "";
829 
830                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
831                         arList.AddRange(ar);
832 
833                         foreach (string str in arList) {
834                             //YES/NO
835                             if (str.StartsWith("Error: ")) {
836                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
837                                     isError = true;
838                                 }
839                             }
840                             if (str.StartsWith("Message: ")) {
841                                 message = str.Replace("Message: ", "");
842                             }
843                             if (str.StartsWith("Details: ")) {
844                                 details = str.Replace("Details: ", "");
845                             }
846 
847                             if (str.StartsWith("NoteID: ")) {
848                                 value = str.Replace("NoteID: ", "");
849                                 if (!string.IsNullOrEmpty(value)) {
850                                     noteId = value;
851                                 }
852                             }
853                             if (str.StartsWith("Unid: ")) {
854                                 value = str.Replace("Unid: ", "");
855                                 if (!string.IsNullOrEmpty(value)) {
856                                     unid = value;
857                                 }
858                             }
859                             if (str.StartsWith("Form: ")) {
860                                 value = str.Replace("Form: ", "");
861                                 if (!string.IsNullOrEmpty(value)) {
862                                     form = value;
863                                 }
864                             }
865 
866                             if (str.StartsWith("Size: ")) {
867                                 value = str.Replace("Size: ", "");
868                                 if (!string.IsNullOrEmpty(value)) {
869                                     size = value;
870                                 }
871                             }
872 
873                             if (str.StartsWith("URL: ")) {
874                                 value = str.Replace("URL: ", "");
875                                 if (!string.IsNullOrEmpty(value)) {
876                                     url = value;
877                                 }
878                             }
879 
880                             if (str.StartsWith("Created: ")) {
881                                 value = str.Replace("Created: ", "");
882                                 if (!string.IsNullOrEmpty(value)) {
883                                     created = value;
884                                 }
885                             }
886 
887                             if (str.StartsWith("Modified: ")) {
888                                 value = str.Replace("Modified: ", "");
889                                 if (!string.IsNullOrEmpty(value)) {
890                                     modified = value;
891                                 }
892                             }
893                         }
894 
895                         if (isError) {
896                             Connector.ReturnMessages.Add(message);
897                             Connector.ReturnMessages.Add(details);
898                             Connector.HasError = true; //throws exception
899                             return false;
900                         } else {
901                             // add doc to list
902                             if (dbObj.Documents == null) {
903                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
904                             }
905                             if (!string.IsNullOrEmpty(unid)) {
906                                 //docObj = new DocumentObject(dbObj, unid);
907                                 docObj.UniversalID = unid;
908                                 docObj.NoteID = noteId;
909                                 docObj.Size = size;
910                                 docObj.Url = url;
911                                 docObj.Form = form;
912                                 docObj.DateCreated = created;
913                                 docObj.DateModified = modified;
914                                 if (dbObj.Documents.ContainsKey(unid)) {
915                                     dbObj.Documents.Remove(unid);
916                                 }
917                                 dbObj.Documents.Add(unid, docObj);
918                                 Connector.ReturnMessages.Add(message);
919                                 Connector.ReturnMessages.Add(details);
920                                 Connector.HasError = false; //SUCCESS
921                             } else {
922                                 // unid not found??
923                                 Connector.ReturnMessages.Add(message);
924                                 Connector.ReturnMessages.Add(details);
925                                 Connector.ReturnMessages.Add("Universal ID not found!");
926                                 Connector.HasError = true; // throws exception
927                             }
928                         }
929                     } else {
930                         Connector.ReturnMessages.Add("Executing Document Request - Unable to decode/decrypt the content of the response!");
931                         Connector.HasError = true;  //throws exception
932                         return false;
933                     }
934                 } else {
935                     //no response from server
936                     Connector.ReturnMessages.Add("Executing Document Request -  Unable to get response from XPages!");
937                     Connector.HasError = true;  //throws exception
938                     return false;
939                 }
940                 return true;
941             } else {
942                 Connector.ReturnMessages.Add("Executing Document Request -  Unable to get the Document - Authentication Issue");
943                 Connector.HasError = true;  //throws exception
944                 return false;
945             }
946         } catch (Exception ex) {
947             if (ex.GetType() == typeof(WebException)) {
948                 WebException webex;
949                 webex = (WebException)ex;
950                 if (webex.Response != null) {
951                     Stream stream = webex.Response.GetResponseStream();
952                     using (StreamReader reader = new StreamReader(stream)) {
953                         string errorResp = reader.ReadToEnd();
954                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
955                         Connector.ReturnMessages.Add("Unable to Execute the Document Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
956                         Connector.HasError = true;  //throws exception
957                         return false;
958                     }
959                 } else {
960                     Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
961                     Connector.HasError = true;  //throws exception
962                     return false;
963                 }
964             } else {
965                 Connector.ReturnMessages.Add("Unable to Execute the Document Request : " + Common.GetErrorInfo(ex));
966                 Connector.HasError = true;  //throws exception
967                 return false;
968             }
969         }
970     }
