1179                                         //fObj.Application + "" + fObj.Creator + "" + fObj.DateCreated + "" + fObj.DateModfied + "$" + fObj.FieldName + "" + fObj.FileExtension + "" + fObj.FileName + "$" + fObj.FileSize + "$" + fObj.LinkToFile + "$" + fObj.Other + "$" + fObj.SoftClass);
1180                                         fObj = new FileObject(docObj);
1181                                         if (fObj.Initialize(str)) {
1182                                             files.Add(fObj.Name, fObj);
1183                                         }
1184                                     }
1185                                 }
1186                                 docObj.Files = files;
1187                                 if (dbObj.Documents.ContainsKey(unid)) {
1188                                     dbObj.Documents.Remove(unid);
1189                                 }
