1214             } else {
1215                 Connector.ReturnMessages.Add("Executing DocumentFiles Request -  Unable to get the Document - Authentication Issue");
1216                 Connector.HasError = true;  //throws exception
1217                 return false;
1218             }
1219         } catch (Exception ex) {
1220             if (ex.GetType() == typeof(WebException)) {
1221                 WebException webex;
1222                 webex = (WebException)ex;
1223                 if (webex.Response != null) {
1224                     Stream stream = webex.Response.GetResponseStream();
1225                     using (StreamReader reader = new StreamReader(stream)) {
1226                         string errorResp = reader.ReadToEnd();
1227                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1228                         Connector.ReturnMessages.Add("Unable to Execute the DocumentFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1229                         Connector.HasError = true;  //throws exception
1230                         return false;
