1245     protected internal bool ExecuteAllDocumentsRequest(string WebServiceURL, string searchField, string searchValue, string formula, string unids, DatabaseObject dbObj) {
1246         HttpWebRequest request = null;
1247 
1248         try {
1249             if (!isInitialized) {
1250                 Connector.ReturnMessages.Add("Unable to execute the All Documents Request - Not Initialized/Connected To Server");
1251                 Connector.HasError = true;
1252                 return false;
1253             }
1254 
1255             //  ServicePointManager.Expect100Continue = false;
1256 
1257             //create an HTTP request
1258 
1259             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllDocuments");
1260             //request.Timeout = 300000; // set timeout to 5minutes
1261             request.Timeout = 1800000; //half an hour!
1262             request.CookieContainer = this.AuthenticationCookie;
1263             //  request.KeepAlive = True
1264             request.AllowAutoRedirect = true;
1265 
1266             request.Method = WebRequestMethods.Http.Post;   //"POST"
1267             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1268                                                                     // Set the request stream
1269             string result;
1270 
1271             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1272             if (!AddIdentityHeader(ref request)) {
1273                 Connector.ReturnMessages.Add("Unable to Execute All Documents Request : Identity Header could not be added!");
1274                 Connector.HasError = true;  //throws error
1275                 return false;
1276             }
1277 
1278             //test with encryption
1279             StringBuilder sb = new StringBuilder();
1280 
1281             sb.AppendLine("UserName: " + Connection.UserName);
1282             sb.AppendLine("ServerName: " + dbObj.ServerName);
1283             sb.AppendLine("FilePath: " + dbObj.FilePath);
1284             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
1285             sb.AppendLine("SearchField: " + searchField);
1286             sb.AppendLine("SearchValue: " + searchValue);
1287             sb.AppendLine("Formula: " + formula);
1288             sb.AppendLine("Unids: " + unids);
1289             sb.AppendLine("Connecting via XPagesAPI");
1290 
1291             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1292             string myString = Encoding.UTF8.GetString(b);
1293             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1294             if (EncodedEncryptedContent.Initialize()) {
1295                 Stream requestStream = null;
1296                 try {
1297                     requestStream = request.GetRequestStream();
1298                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
1299                         writer.Write(EncodedEncryptedContent.EncodedContent);
1300                     }
1301                 } catch (Exception e) {
1302                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1303                     Connector.HasError = true;  //throws exception
1304                     return false;
1305                 } finally {
1306                     //if (requestStream != null) {
1307                     //    requestStream.Dispose();
1308                     //}
1309                 }
1310             } else {
1311                 Connector.ReturnMessages.Add("Executing All Documents Request - Unable to encode/encrypt the content of the request!");
1312                 Connector.HasError = true; //throws exception
1313                 return false;
1314             }
1315 
1316             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1317 
1318             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
1319                 Stream responseStream = null;
1320                 try {
1321                     responseStream = request.GetResponse().GetResponseStream();
1322                     using (StreamReader reader = new StreamReader(responseStream, true)) {
1323                         result = reader.ReadToEnd();
1324                     }
1325                 } catch (Exception e) {
1326                     Connector.ReturnMessages.Add("Unable to execute the All Documents Request : " + Common.GetErrorInfo(e));
1327                     Connector.HasError = true;  //throws exception
1328                     return false;
1329                 } finally {
1330                     //if (responseStream != null) {
1331                     //    responseStream.Dispose();
1332                     //}
1333                 }
1334 
1335                 if (result != null) {
1336                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1337                     if (DecodeDecryptedContent.Initialize()) {
1338                         ArrayList arList = new ArrayList();
1339 
1340                         string[] ar = null;
1341                         bool isError = false;
1342 
1343                         string message = "";
1344                         string details = "";
1345                         string value = "";
1346                         string[] sep = { "||" };
1347                         string[] NoteIds = { };
1348                         string[] Unids = { };
1349                         string[] Forms = { };
1350                         string[] Sizes = { };
1351                         string[] URLs = { };
1352                         string[] Created = { };
1353                         string[] Modified = { };
1354 
1355                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1356                         arList.AddRange(ar);
1357                         foreach (string str in arList) {
1358                             //YES/NO
1359                             if (str.StartsWith("Error: ")) {
1360                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
1361                                     isError = true;
1362                                 }
1363                             }
1364                             if (str.StartsWith("Message: ")) {
1365                                 message = str.Replace("Message: ", "");
1366                             }
1367                             if (str.StartsWith("Details: ")) {
1368                                 details = str.Replace("Details: ", "");
1369                             }
1370 
1371                             if (str.StartsWith("Unids: ")) {
1372                                 value = str.Replace("Unids: ", "");
1373                                 if (!string.IsNullOrEmpty(value)) {
1374                                     Unids = value.Split(sep, StringSplitOptions.None);
1375                                 }
1376                             }
1377 
1378                             if (str.StartsWith("NoteIDs: ")) {
1379                                 value = str.Replace("NoteIDs: ", "");
1380                                 if (!string.IsNullOrEmpty(value)) {
1381                                     NoteIds = value.Split(sep, StringSplitOptions.None);
1382                                 }
1383                             }
1384 
1385                             if (str.StartsWith("Forms: ")) {
1386                                 value = str.Replace("Forms: ", "");
1387                                 if (!string.IsNullOrEmpty(value)) {
1388                                     Forms = value.Split(sep, StringSplitOptions.None);
1389                                 }
1390                             }
1391 
1392                             if (str.StartsWith("Sizes: ")) {
1393                                 value = str.Replace("Sizes: ", "");
1394                                 if (!string.IsNullOrEmpty(value)) {
1395                                     Sizes = value.Split(sep, StringSplitOptions.None);
1396                                 }
1397                             }
1398 
1399                             if (str.StartsWith("URLs: ")) {
1400                                 value = str.Replace("URLs: ", "");
1401                                 if (!string.IsNullOrEmpty(value)) {
1402                                     URLs = value.Split(sep, StringSplitOptions.None);
1403                                 }
1404                             }
1405 
1406                             if (str.StartsWith("Created: ")) {
1407                                 value = str.Replace("Created: ", "");
1408                                 if (!string.IsNullOrEmpty(value)) {
1409                                     Created = value.Split(sep, StringSplitOptions.None);
1410                                 }
1411                             }
1412 
1413                             if (str.StartsWith("Modified: ")) {
1414                                 value = str.Replace("Modified: ", "");
1415                                 if (!string.IsNullOrEmpty(value)) {
1416                                     Modified = value.Split(sep, StringSplitOptions.None);
1417                                 }
1418                             }
1419                         }
1420 
1421                         //loop through unids, notesids, forms, sizes, urls and create document objects
1422                         // check if all have the same number of items!
1423                         int count = 0;
1424                         if (Unids != null && Unids.Length > 0) {
1425                             count = Unids.Length;
1426                             if (NoteIds != null && NoteIds.Length > 0) {
1427                                 if (count != NoteIds.Length) {
1428                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> NoteIds");
1429                                     Connector.HasError = true;  //throws exception
1430                                     return false;
1431                                 } else {
1432                                     // form can be empty!!
1433                                     //if (Forms != null && Forms.Length > 0) {
1434                                     //    if (count != Forms.Length) {
1435                                     //        Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Forms");
1436                                     //        Connector.hasError = true;  //throws exception
1437                                     //        return false;
1438                                     //    } else {
1439                                     if (Sizes != null && Sizes.Length > 0) {
1440                                         if (count != Sizes.Length) {
1441                                             Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> Sizes");
1442                                             Connector.HasError = true;  //throws exception
1443                                             return false;
1444                                         } else {
1445                                             if (URLs != null && URLs.Length > 0) {
1446                                                 if (count != URLs.Length) {
1447                                                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Documents - Unids <> URLs");
1448                                                     Connector.HasError = true;  //throws exception
1449                                                     return false;
1450                                                 }
1451                                             }
1452                                         }
1453                                     }
1454                                     //   }
1455                                     // }
1456                                 }
1457                             }
1458                             // loop through and create doc obj
1459                             DocumentObject docObj = null;
1460                             if (dbObj.Documents == null) {
1461                                 dbObj.Documents = new Dictionary<string, DocumentObject>();
1462                             }
1463                             for (int i = 0; i < Unids.Length; i++) {
1464                                 docObj = new DocumentObject(dbObj, Unids[i]);
1465                                 if (NoteIds.Length - 1 >= i) {
1466                                     docObj.NoteID = NoteIds[i];
1467                                 }
1468                                 if (Forms.Length - 1 >= i) {
1469                                     docObj.Form = Forms[i];
1470                                 }
1471                                 if (Sizes.Length - 1 >= i) {
1472                                     docObj.Size = Sizes[i];
1473                                 }
1474                                 if (URLs.Length - 1 >= i) {
1475                                     docObj.Url = URLs[i];
1476                                 }
1477                                 if (Created.Length - 1 >= i) {
1478                                     docObj.DateCreated = Created[i];
1479                                 }
1480                                 if (Modified.Length - 1 >= i) {
1481                                     docObj.DateModified = Modified[i];
1482                                 }
1483                                 if (dbObj.Documents.ContainsKey(Unids[i])) {
1484                                     dbObj.Documents.Remove(Unids[i]);
1485                                 }
1486                                 docObj.IsInitialized = true;
1487                                 dbObj.Documents.Add(Unids[i], docObj);
1488                             }
1489                         }
1490 
1491                         if (isError) {
1492                             Connector.ReturnMessages.Add(message);
1493                             Connector.ReturnMessages.Add(details);
1494                             Connector.HasError = true; //throws exception
1495                             return false;
1496                         } else {
1497                             Connector.ReturnMessages.Add(message);
1498                             Connector.ReturnMessages.Add(details);
1499                             Connector.HasError = false; //SUCCESS
1500                         }
1501                     } else {
1502                         Connector.ReturnMessages.Add("Executing All Documents Request - Unable to decode/decrypt the content of the response!");
1503                         Connector.HasError = true;  //throws exception
1504                         return false;
1505                     }
1506                 } else {
1507                     //no response from server
1508                     Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get response from XPages!");
1509                     Connector.HasError = true;  //throws exception
1510                     return false;
1511                 }
1512                 return true;
1513             } else {
1514                 Connector.ReturnMessages.Add("Executing All Documents Request -  Unable to get the Document - Authentication Issue");
1515                 Connector.HasError = true;  //throws exception
1516                 return false;
1517             }
1518         } catch (Exception ex) {
1519             if (ex.GetType() == typeof(WebException)) {
1520                 WebException webex;
1521                 webex = (WebException)ex;
1522                 if (webex.Response != null) {
1523                     Stream stream = webex.Response.GetResponseStream();
1524                     using (StreamReader reader = new StreamReader(stream)) {
1525                         string errorResp = reader.ReadToEnd();
1526                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1527                         Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1528                         Connector.HasError = true;  //throws exception
1529                         return false;
1530                     }
1531                 } else {
1532                     Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1533                     Connector.HasError = true;  //throws exception
1534                     return false;
1535                 }
1536             } else {
1537                 Connector.ReturnMessages.Add("Unable to Execute the All Documents Request : " + Common.GetErrorInfo(ex));
1538                 Connector.HasError = true;  //throws exception
1539                 return false;
1540             }
1541         }
1542     }
