1810                                 }
1811                                 docObj.IsInitialized = true;
1812                                 dbObj.Documents.Add(Unids[i], docObj);
1813                             }
1814                         }
1815 
1816                         if (isError) {
1817                             Connector.ReturnMessages.Add(message);
1818                             Connector.ReturnMessages.Add(details);
1819                             Connector.HasError = true; //throws exception
1820                             return false;
1821                         } else {
1822                             Connector.ReturnMessages.Add(message);
1823                             Connector.ReturnMessages.Add(details);
1824                             Connector.HasError = false; //SUCCESS
1825                         }
1826                     } else {
1827                         Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request - Unable to decode/decrypt the content of the response!");
1828                         Connector.HasError = true;  //throws exception
1829                         return false;
1830                     }
1831                 } else {
1832                     //no response from server
1833                     Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get response from XPages!");
1834                     Connector.HasError = true;  //throws exception
1835                     return false;
1836                 }
1837                 return true;
1838             } else {
1839                 Connector.ReturnMessages.Add("Executing AllDocumentsFiles Request -  Unable to get the Document - Authentication Issue");
1840                 Connector.HasError = true;  //throws exception
1841                 return false;
1842             }
1843         } catch (Exception ex) {
1844             if (ex.GetType() == typeof(WebException)) {
1845                 WebException webex;
1846                 webex = (WebException)ex;
1847                 if (webex.Response != null) {
1848                     Stream stream = webex.Response.GetResponseStream();
1849                     using (StreamReader reader = new StreamReader(stream)) {
1850                         string errorResp = reader.ReadToEnd();
1851                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
1852                         Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
1853                         Connector.HasError = true;  //throws exception
1854                         return false;
1855                     }
1856                 } else {
1857                     Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
1858                     Connector.HasError = true;  //throws exception
1859                     return false;
1860                 }
1861             } else {
1862                 Connector.ReturnMessages.Add("Unable to Execute the AllDocumentsFiles Request : " + Common.GetErrorInfo(ex));
1863                 Connector.HasError = true;  //throws exception
1864                 return false;
1865             }
