1869     protected internal bool ExecuteFieldsRequest(string WebServiceURL, DocumentObject docObj, string fields) {
1870         HttpWebRequest request = null;
1871 
1872         try {
1873             if (!isInitialized) {
1874                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
1875                 Connector.HasError = true;
1876                 return false;
1877             }
1878 
1879             // ServicePointManager.Expect100Continue = false;
1880 
1881             //create an HTTP request
1882 
1883             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_Fields");
1884 
1885             request.CookieContainer = this.AuthenticationCookie;
1886             //  request.KeepAlive = True
1887             request.AllowAutoRedirect = true;
1888 
1889             request.Method = WebRequestMethods.Http.Post;   //"POST"
1890             request.ContentType = "application/jpi; charset=utf-8"; //application/json
1891                                                                     // Set the request stream
1892             string result;
1893 
1894             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
1895             if (!AddIdentityHeader(ref request)) {
1896                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
1897                 Connector.HasError = true;  //throws error
1898                 return false;
1899             }
1900 
1901             //test with encryption
1902             StringBuilder sb = new StringBuilder();
1903 
1904             sb.AppendLine("UserName: " + Connection.UserName);
1905             sb.AppendLine("ServerName: " + docObj.Database.ServerName);
1906             sb.AppendLine("FilePath: " + docObj.Database.FilePath);
1907             sb.AppendLine("ReplicationID: " + docObj.Database.ReplicationID);
1908             sb.AppendLine("UniversalID: " + docObj.UniversalID);
1909             sb.AppendLine("Fields: " + fields);
1910             sb.AppendLine("Connecting via XPagesAPI");
1911 
1912             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
1913             string myString = Encoding.UTF8.GetString(b);
1914             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
1915             if (EncodedEncryptedContent.Initialize()) {
1916                 Stream requestStream = null;
1917                 try {
1918                     requestStream = request.GetRequestStream();
1919                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
1920                         writer.Write(EncodedEncryptedContent.EncodedContent);
1921                     }
1922                 } catch (Exception e) {
1923                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
1924                     Connector.HasError = true;  //throws exception
1925                     return false;
1926                 } finally {
1927                     //if (requestStream != null) {
1928                     //    requestStream.Dispose();
1929                     //}
1930                 }
1931             } else {
1932                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
1933                 Connector.HasError = true; //throws exception
1934                 return false;
1935             }
1936 
1937             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
1938 
1939             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
1940                 Stream responseStream = null;
1941                 try {
1942                     responseStream = request.GetResponse().GetResponseStream();
1943                     using (StreamReader reader = new StreamReader(responseStream, true)) {
1944                         result = reader.ReadToEnd();
1945                     }
1946                 } catch (Exception e) {
1947                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
1948                     Connector.HasError = true;  //throws exception
1949                     return false;
1950                 } finally {
1951                     //if (responseStream != null) {
1952                     //    responseStream.Dispose();
1953                     //}
1954                 }
1955 
1956                 if (result != null) {
1957                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
1958                     if (DecodeDecryptedContent.Initialize()) {
1959                         ArrayList arList = new ArrayList();
1960 
1961                         string[] ar = null;
1962                         bool isError = false;
1963 
1964                         string message = "";
1965                         string details = "";
1966                         string value = "";
1967                         string[] fieldnames = null;
1968                         string[] fieldvalues = null;
1969                         string[] fieldtypes = null;
1970 
1971                         string[] sep = { "||" };
1972 
1973                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
1974                         arList.AddRange(ar);
1975                         foreach (string str in arList) {
1976                             //YES/NO
1977                             if (str.StartsWith("Error: ")) {
1978                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
1979                                     isError = true;
1980                                 }
1981                             }
1982                             if (str.StartsWith("Message: ")) {
1983                                 message = str.Replace("Message: ", "");
1984                             }
1985                             if (str.StartsWith("Details: ")) {
1986                                 details = str.Replace("Details: ", "");
1987                             }
1988 
1989                             if (str.StartsWith("FieldNames: ")) {
1990                                 value = str.Replace("FieldNames: ", "");
1991                                 if (!string.IsNullOrEmpty(value)) {
1992                                     fieldnames = value.Split(sep, StringSplitOptions.None);
1993                                 }
1994                             }
1995 
1996                             if (str.StartsWith("FieldValues: ")) {
1997                                 value = str.Replace("FieldValues: ", "");
1998                                 if (!string.IsNullOrEmpty(value)) {
1999                                     fieldvalues = value.Split(sep, StringSplitOptions.None);
2000                                 }
2001                             }
2002 
2003                             if (str.StartsWith("FieldTypes: ")) {
2004                                 value = str.Replace("FieldTypes: ", "");
2005                                 if (!string.IsNullOrEmpty(value)) {
2006                                     fieldtypes = value.Split(sep, StringSplitOptions.None);
2007                                 }
2008                             }
2009                         }
2010                         FieldObject fObj = null;
2011                         if (docObj.Fields == null) {
2012                             docObj.Fields = new SortedDictionary<string, FieldObject>();
2013                         }
2014                         for (int i = 0; i < fieldnames.Length; i++) {
2015                             fObj = new FieldObject(fieldnames[i]);
2016                             if (fieldvalues.Length - 1 >= i) {
2017                                 fObj.Value = fieldvalues[i];
2018                             }
2019                             if (fieldtypes.Length - 1 >= i) {
2020                                 fObj.Type = fieldtypes[i];
2021                             }
2022 
2023                             if (docObj.Fields.ContainsKey(fObj.Name)) {
2024                                 docObj.Fields.Remove(fObj.Name);
2025                             }
2026                             docObj.Fields.Add(fObj.Name, fObj);
2027                         }
2028 
2029                         if (isError) {
2030                             Connector.ReturnMessages.Add(message);
2031                             Connector.ReturnMessages.Add(details);
2032                             Connector.HasError = true; //throws exception
2033                             return false;
2034                         } else {
2035                             Connector.ReturnMessages.Add(message);
2036                             Connector.ReturnMessages.Add(details);
2037                             Connector.HasError = false; //SUCCESS
2038                         }
2039                     } else {
2040                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2041                         Connector.HasError = true;  //throws exception
2042                         return false;
2043                     }
2044                 } else {
2045                     //no response from server
2046                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2047                     Connector.HasError = true;  //throws exception
2048                     return false;
2049                 }
2050                 return true;
2051             } else {
2052                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2053                 Connector.HasError = true;  //throws exception
2054                 return false;
2055             }
2056         } catch (Exception ex) {
2057             if (ex.GetType() == typeof(WebException)) {
2058                 WebException webex;
2059                 webex = (WebException)ex;
2060                 if (webex.Response != null) {
2061                     Stream stream = webex.Response.GetResponseStream();
2062                     using (StreamReader reader = new StreamReader(stream)) {
2063                         string errorResp = reader.ReadToEnd();
2064                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2065                         Connector.ReturnMessages.Add("Unable to Execute the Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2066                         Connector.HasError = true;  //throws exception
2067                         return false;
2068                     }
2069                 } else {
2070                     Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2071                     Connector.HasError = true;  //throws exception
2072                     return false;
2073                 }
2074             } else {
2075                 Connector.ReturnMessages.Add("Unable to Execute the Fields Request : " + Common.GetErrorInfo(ex));
2076                 Connector.HasError = true;  //throws exception
2077                 return false;
2078             }
2079         }
2080     }
