2389                 webex = (WebException)ex;
2390                 if (webex.Response != null) {
2391                     Stream stream = webex.Response.GetResponseStream();
2392                     using (StreamReader reader = new StreamReader(stream)) {
2393                         string errorResp = reader.ReadToEnd();
2394                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2395                         Connector.ReturnMessages.Add("Unable to Execute the All Files Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2396                         Connector.HasError = true;  //throws exception
2397                         return false;
2398                     }
2399                 } else {
2400                     Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2401                     Connector.HasError = true;  //throws exception
2402                     return false;
2403                 }
2404             } else {
2405                 Connector.ReturnMessages.Add("Unable to Execute the All Files Request : " + Common.GetErrorInfo(ex));
2406                 Connector.HasError = true;  //throws exception
2407                 return false;
2408             }
2409         }
2410     }
