2412     protected internal bool ExecuteAllFieldsRequest(string WebServiceURL, DatabaseObject dbObj, string fields) {
2413         HttpWebRequest request = null;
2414 
2415         try {
2416             if (!isInitialized) {
2417                 Connector.ReturnMessages.Add("Unable to execute the Fields Request - Not Initialized/Connected To Server");
2418                 Connector.HasError = true;
2419                 return false;
2420             }
2421 
2422             // ServicePointManager.Expect100Continue = false;
2423 
2424             //create an HTTP request
2425 
2426             request = (HttpWebRequest)WebRequest.Create(WebServiceURL + "?API_AllFields");
2427             // request.Timeout = 300000; // set timeout to 5minutes
2428             request.Timeout = 1800000; //half an hour!
2429             request.CookieContainer = this.AuthenticationCookie;
2430             //  request.KeepAlive = True
2431             request.AllowAutoRedirect = true;
2432 
2433             request.Method = WebRequestMethods.Http.Post;   //"POST"
2434             request.ContentType = "application/jpi; charset=utf-8"; //application/json
2435                                                                     // Set the request stream
2436             string result;
2437 
2438             //add a identifier in the header to be use in JPI Service to check if the request is coming from a valid source aka JPI XPages Connector
2439             if (!AddIdentityHeader(ref request)) {
2440                 Connector.ReturnMessages.Add("Unable to Execute Fields Request : Identity Header could not be added!");
2441                 Connector.HasError = true;  //throws error
2442                 return false;
2443             }
2444 
2445             //test with encryption
2446             StringBuilder sb = new StringBuilder();
2447 
2448             sb.AppendLine("UserName: " + Connection.UserName);
2449             sb.AppendLine("ServerName: " + dbObj.ServerName);
2450             sb.AppendLine("FilePath: " + dbObj.FilePath);
2451             sb.AppendLine("ReplicationID: " + dbObj.ReplicationID);
2452             string listUnids = String.Join(";", dbObj.Documents.Keys.ToArray());
2453 
2454             //foreach (KeyValuePair<string, DocumentObject> kvp in dbObj.Documents) {
2455             //    listUnids = listUnids + kvp.Key + ";";
2456             //}
2457             //listUnids = listUnids.Substring(0, listUnids.Length - 1); // remove ; at the end
2458 
2459             sb.AppendLine("UniversalIDs: " + listUnids);
2460             sb.AppendLine("Fields: " + fields);
2461 
2462             sb.AppendLine("Connecting via XPagesAPI");
2463 
2464             byte[] b = Encoding.Default.GetBytes(sb.ToString().Replace(Environment.NewLine, " | "));
2465             string myString = Encoding.UTF8.GetString(b);
2466             Encryptor EncodedEncryptedContent = new Encryptor(myString, true, ref Connection);
2467             if (EncodedEncryptedContent.Initialize()) {
2468                 Stream requestStream = null;
2469                 try {
2470                     requestStream = request.GetRequestStream();
2471                     using (StreamWriter writer = new StreamWriter(requestStream, Encoding.UTF8)) {
2472                         writer.Write(EncodedEncryptedContent.EncodedContent);
2473                     }
2474                 } catch (Exception e) {
2475                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2476                     Connector.HasError = true;  //throws exception
2477                     return false;
2478                 } finally {
2479                     //if (requestStream != null) {
2480                     //    requestStream.Dispose();
2481                     //}
2482                 }
2483             } else {
2484                 Connector.ReturnMessages.Add("Executing Fields Request - Unable to encode/encrypt the content of the request!");
2485                 Connector.HasError = true; //throws exception
2486                 return false;
2487             }
2488 
2489             HttpWebResponse response = (HttpWebResponse)request.GetResponse();  //can give webexception error 500
2490 
2491             if ((response.StatusCode == HttpStatusCode.OK) || (response.StatusCode == HttpStatusCode.Found) || (response.StatusCode == HttpStatusCode.Redirect) || (response.StatusCode == HttpStatusCode.Moved) || (response.StatusCode == HttpStatusCode.MovedPermanently)) {
2492                 Stream responseStream = null;
2493                 try {
2494                     responseStream = request.GetResponse().GetResponseStream();
2495                     using (StreamReader reader = new StreamReader(responseStream, true)) {
2496                         result = reader.ReadToEnd();
2497                     }
2498                 } catch (Exception e) {
2499                     Connector.ReturnMessages.Add("Unable to execute the Request : " + Common.GetErrorInfo(e));
2500                     Connector.HasError = true;  //throws exception
2501                     return false;
2502                 } finally {
2503                     //if (responseStream != null) {
2504                     //    responseStream.Dispose();
2505                     //}
2506                 }
2507 
2508                 if (result != null) {
2509                     Encryptor DecodeDecryptedContent = new Encryptor(result, false, ref Connection);
2510                     if (DecodeDecryptedContent.Initialize()) {
2511                         ArrayList arList = new ArrayList();
2512 
2513                         string[] ar = null;
2514                         bool isError = false;
2515 
2516                         string message = "";
2517                         string details = "";
2518                         string value = "";
2519                         string unid = "";
2520                         Dictionary<string, List<string[]>> dict = new Dictionary<string, List<string[]>>();
2521                         string[] fieldnames = null;
2522                         string[] fieldvalues = null;
2523                         string[] fieldtypes = null;
2524 
2525                         string[] sep = { "||" };
2526 
2527                         ar = DecodeDecryptedContent.DecodedContent.Split(new[] { " | " }, StringSplitOptions.None);
2528                         arList.AddRange(ar);
2529                         foreach (string str in arList) {
2530                             //YES/NO
2531                             if (str.StartsWith("Error: ")) {
2532                                 if (str.Replace("Error: ", "").Equals("Y", StringComparison.OrdinalIgnoreCase)) {
2533                                     isError = true;
2534                                 }
2535                             }
2536                             if (str.StartsWith("Message: ")) {
2537                                 message = str.Replace("Message: ", "");
2538                             }
2539                             if (str.StartsWith("Details: ")) {
2540                                 details = str.Replace("Details: ", "");
2541                             }
2542 
2543                             // 'FieldNames: Unid: '
2544                             if (str.StartsWith("FieldNames: ")) {
2545                                 value = str.Replace("FieldNames: ", "");
2546                                 //get the unid C125760F0054EEFBC1257356002F192D
2547                                 if (value.Length > 34) { //Unid is 32 + ': '
2548                                     unid = value.Substring(0, 32);
2549                                     //remove unid
2550                                     value = value.Remove(0, 34);
2551                                     if (!string.IsNullOrEmpty(value)) {
2552                                         fieldnames = value.Split(sep, StringSplitOptions.None);
2553                                         // check if item is already in dict
2554                                         if (!dict.ContainsKey(unid)) {
2555                                             dict.Add(unid, new List<string[]>());
2556                                         }
2557                                         dict[unid].Add(fieldnames);
2558                                     }
2559                                 }// else something is wrong
2560                             }
2561 
2562                             if (str.StartsWith("FieldValues: ")) {
2563                                 value = str.Replace("FieldValues: ", "");
2564                                 if (value.Length > 34) { //Unid is 32 + ': '
2565                                     unid = value.Substring(0, 32);
2566                                     //remove unid
2567                                     value = value.Remove(0, 34);
2568                                     if (!string.IsNullOrEmpty(value)) {
2569                                         fieldvalues = value.Split(sep, StringSplitOptions.None);
2570                                         // check if item is already in dict
2571                                         if (!dict.ContainsKey(unid)) {
2572                                             dict.Add(unid, new List<string[]>());
2573                                         }
2574                                         dict[unid].Add(fieldvalues);
2575                                     }
2576                                 }
2577                             }
2578 
2579                             if (str.StartsWith("FieldTypes: ")) {
2580                                 value = str.Replace("FieldTypes: ", "");
2581                                 if (value.Length > 34) { //Unid is 32 + ': '
2582                                     unid = value.Substring(0, 32);
2583                                     //remove unid
2584                                     value = value.Remove(0, 34);
2585                                     if (!string.IsNullOrEmpty(value)) {
2586                                         fieldtypes = value.Split(sep, StringSplitOptions.None);
2587                                         // check if item is already in dict
2588                                         if (!dict.ContainsKey(unid)) {
2589                                             dict.Add(unid, new List<string[]>());
2590                                         }
2591                                         dict[unid].Add(fieldtypes);
2592                                     }
2593                                 }
2594                             }
2595                         }
2596 
2597                         DocumentObject docObj = null;
2598                         FieldObject fObj = null;
2599 
2600                         foreach (KeyValuePair<string, List<string[]>> kvp in dict) {
2601                             if (dbObj.Documents.ContainsKey(kvp.Key)) {
2602                                 docObj = dbObj.Documents[kvp.Key];
2603                                 if (docObj.Fields == null) {
2604                                     docObj.Fields = new SortedDictionary<string, FieldObject>();
2605                                 }
2606                                 //reset
2607                                 fieldnames = null;
2608                                 fieldvalues = null;
2609                                 fieldtypes = null;
2610                                 // first array in list should be fieldnames, then values then types
2611                                 fieldnames = kvp.Value[0];
2612                                 fieldvalues = kvp.Value[1];
2613                                 fieldtypes = kvp.Value[2];
2614 
2615                                 for (int i = 0; i < fieldnames.Length; i++) {//fieldnames
2616                                     fObj = new FieldObject(fieldnames[i]);
2617                                     if (fieldvalues.Length - 1 >= i) {
2618                                         fObj.Value = fieldvalues[i];
2619                                     }
2620                                     if (fieldtypes.Length - 1 >= i) {
2621                                         fObj.Type = fieldtypes[i];
2622                                     }
2623 
2624                                     if (docObj.Fields.ContainsKey(fObj.Name)) {
2625                                         docObj.Fields.Remove(fObj.Name);
2626                                     }
2627                                     docObj.Fields.Add(fObj.Name, fObj);
2628                                 }
2629                             } else {
2630                                 Connector.ReturnMessages.Add("Unable to find the document in the databaseobject :" + kvp.Key);
2631                                 isError = true;
2632                             }
2633                         }
2634 
2635                         if (isError) {
2636                             Connector.ReturnMessages.Add(message);
2637                             Connector.ReturnMessages.Add(details);
2638                             Connector.HasError = true; //throws exception
2639                             return false;
2640                         } else {
2641                             Connector.ReturnMessages.Add(message);
2642                             Connector.ReturnMessages.Add(details);
2643                             Connector.HasError = false; //SUCCESS
2644                         }
2645                     } else {
2646                         Connector.ReturnMessages.Add("Executing Fields Request - Unable to decode/decrypt the content of the response!");
2647                         Connector.HasError = true;  //throws exception
2648                         return false;
2649                     }
2650                 } else {
2651                     //no response from server
2652                     Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get response from XPages!");
2653                     Connector.HasError = true;  //throws exception
2654                     return false;
2655                 }
2656                 return true;
2657             } else {
2658                 Connector.ReturnMessages.Add("Executing Fields Request -  Unable to get the Document - Authentication Issue");
2659                 Connector.HasError = true;  //throws exception
2660                 return false;
2661             }
2662         } catch (Exception ex) {
2663             if (ex.GetType() == typeof(WebException)) {
2664                 WebException webex;
2665                 webex = (WebException)ex;
2666                 if (webex.Response != null) {
2667                     Stream stream = webex.Response.GetResponseStream();
2668                     using (StreamReader reader = new StreamReader(stream)) {
2669                         string errorResp = reader.ReadToEnd();
2670                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2671                         Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2672                         Connector.HasError = true;  //throws exception
2673                         return false;
2674                     }
2675                 } else {
2676                     Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : " + Common.GetErrorInfo(ex));
2677                     Connector.HasError = true;  //throws exception
2678                     return false;
2679                 }
2680             } else {
2681                 Connector.ReturnMessages.Add("Unable to Execute the All Fields Request : " + Common.GetErrorInfo(ex));
2682                 Connector.HasError = true;  //throws exception
2683                 return false;
2684             }
2685         }
2686     }
