2688     protected internal bool ExecuteGetFileRequest(string FileURL, string LocalFilePath) {
2689         HttpWebRequest request = null;
2690         try {
2691             if (!isInitialized) {
2692                 Connector.ReturnMessages.Add("Unable to execute the Get File Request - Not Initialized/Connected To Server");
2693                 Connector.HasError = true;
2694                 return false;
2695             }
2696 
2697             request = request = (HttpWebRequest)WebRequest.Create(FileURL);
2698 
2699             request.CookieContainer = AuthenticationCookie;
2700             request.AllowAutoRedirect = true;
2701             request.Method = WebRequestMethods.Http.Get;
2702             const int BUFFER_SIZE = 16 * 1024;
2703             byte[] buffer = new byte[BUFFER_SIZE - 1];
2704             int bytesRead;
2705 
2706             using (var outputFileStream = File.Create(LocalFilePath, BUFFER_SIZE)) {
2707                 using (var response2 = request.GetResponse()) {
2708                     using (var responseStream = response2.GetResponseStream()) {
2709                         while ((bytesRead = responseStream.Read(buffer, 0, buffer.Length)) > 0) {
2710                             outputFileStream.Write(buffer, 0, bytesRead);
2711                         }
2712                     }
2713                 }
2714             }
2715             return true;
2716         } catch (Exception ex) {
2717             if (ex.GetType() == typeof(WebException)) {
2718                 WebException webex;
2719                 webex = (WebException)ex;
2720                 if (webex.Response != null) {
2721                     Stream stream = webex.Response.GetResponseStream();
2722                     using (StreamReader reader = new StreamReader(stream)) {
2723                         string errorResp = reader.ReadToEnd();
2724                         errorResp = errorResp.Replace("{", "").Replace("}", "").Replace("\\\\r\\\\n\\\\", Environment.NewLine).Replace(",", Environment.NewLine).Replace(((char)34).ToString(), "").Replace("\\\\r\\\\n", Environment.NewLine);
2725                         Connector.ReturnMessages.Add("Unable to Execute the Get File Request : Invalid request or not authenticated : " + Environment.NewLine + "Web Request Error Response : " + errorResp);
2726                         Connector.HasError = true;  //throws exception
2727                         return false;
2728                     }
2729                 } else {
2730                     Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
2731                     Connector.HasError = true;  //throws exception
2732                     return false;
2733                 }
2734             } else {
2735                 Connector.ReturnMessages.Add("Unable to Execute the Get File Request : " + Common.GetErrorInfo(ex));
2736                 Connector.HasError = true;  //throws exception
2737                 return false;
2738             }
2739         }
2740     }
